Scripts.OnHide:
if ( this.hasStackSplit and (this.hasStackSplit == 1) ) then
	StackSplitFrame:Hide();
end

Scripts.PreClick:
if (EngBot_AtBank == 0) then
	if ( ChatFrameEditBox:IsVisible() ) then
		bar,position,itm = EngBot_GetBarPositionAndCache();
		ChatFrameEditBox:Insert(itm["iteminfo"]);
	end
end

Scripts.OnHide:
if ( this.hasStackSplit and (this.hasStackSplit == 1) ) then
	StackSplitFrame:Hide();
end

Scripts.OnEnter:
if (EngBotConfig["moveLock"] == 0) then
	GameTooltip_AddNewbieTip("Window Dragging: Off", 1.0, 1.0, 1.0, "Window is currently locked in place.");
else
	GameTooltip_AddNewbieTip("Window Dragging: On", 1.0, 1.0, 1.0, "Window is currently unlocked.");
end

