                --
                this:RegisterEvent("CHAT_MSG_WHISPER");
                this:RegisterEvent("PLAYER_TARGET_CHANGED");
                this:RegisterEvent("TRADE_CLOSED");
                this:RegisterEvent("TRADE_UPDATE");

