Scripts.OnHide:
if ( this.hasStackSplit and (this.hasStackSplit == 1) ) then
	StackSplitFrame:Hide();
end

if ( this.hasStackSplit and (this.hasStackSplit == 1) ) then
	StackSplitFrame:Hide();
end

Scripts.OnEnter:
if (EngInventoryConfig["moveLock"] == 0) then
	GameTooltip_AddNewbieTip("Window Dragging: Off", 1.0, 1.0, 1.0, "Window is currently locked in place.");
else
	GameTooltip_AddNewbieTip("Window Dragging: On", 1.0, 1.0, 1.0, "Window is currently unlocked.");
end

Scripts.OnTextChanged:
EngInventory_window_update_required = ENGINVENTORY_MANDATORY;
EngBank_window_update_required = ENGINVENTORY_MANDATORY;
EngInventory_UpdateWindow();
EngBank_UpdateWindow();

Scripts.OnEvent:

if (event == "VARIABLES_LOADED") then 
	this:RegisterEvent("BAG_UPDATE");
	this:RegisterEvent("BAG_UPDATE_COOLDOWN");
	this:RegisterEvent("ITEM_LOCK_CHANGED");
	this:RegisterEvent("UPDATE_INVENTORY_ALERTS");
	this:RegisterEvent("CRAFT_SHOW");
	this:RegisterEvent("TRADE_SKILL_SHOW");
	EngInventory_init();
else
	EngInventory_OnEvent(event);
end
